  @Test
  public void switchOwner() {
    Syria.setOwner(Rome);
    Syria.addMyUnit(archer);
    assertEquals(Syria.getOwnerName(), "Rome");
    assertEquals(archer.getFaction(), "Rome");
    Syria.setOwner(Gaul);
    assertEquals(Syria.getOwnerName(), "Gaul");
    assertEquals(archer.getFaction(), "Gaul");
  }

  @Test
  public void spendTest() {
    Rome.setTreasury(100);
    Syria.setOwner(Rome);
    assertEquals(Syria.spend(100), true);
    assertEquals(Syria.spend(1), false);
  }

  @Test
  public void moveUnitTest() {
    brita.addMyUnit(archer);
    assertEquals(brita.getDistance(lu, content), 1);
    assertEquals(archer.getRemainMovePoint(), 10);
    assertEquals(archer.getInitialMovePoint(), 10);

    assertEquals(lu.getMyUnits().size(), 0);
    assertEquals(brita.moveUnit(archer, lu, content), true);
    assertEquals(archer.getRemainMovePoint(), 9);
    assertEquals(lu.getMyUnits().size(), 1);

    assertEquals(lu.moveUnit(archer, brita, content), true);

    //can not move directly into enemy province
    brita.addEnemyProvince("Lugdunensis");
    assertEquals(brita.moveUnit(archer, lu, content), false);
    assertEquals(brita.getDistance(ge, content), 0);
    brita.removeEnemyProvince("Lugdunensis");

    //unreacheable province
    assertEquals(brita.getDistance(ge, content), 2);
    assertEquals(brita.moveUnit(archer, ge, content), true);
    assertEquals(ge.moveUnit(archer, brita, content), true);
  }
  @Test
  public void moveUnits() {
    ArrayList<Unit> unitSet = new ArrayList<>();
    unitSet.add(archer); unitSet.add(ballista); unitSet.add(lancer);
    brita.addMyUnit(archer);
    brita.addMyUnit(ballista);
    brita.addMyUnit(lancer);
    archer.setRemainMovePoint(0);
    brita.addMyUnits(unitSet);
    assertEquals(brita.moveUnits(unitSet, lu, content), false);
    brita.updateByTurn();
    assertEquals(brita.moveUnits(unitSet, lu, content), true); //1 point
    
    lu.moveUnits(unitSet, brita, content); //1 point

    //can not move directly into enemy province
    brita.addEnemyProvince("Lugdunensis");
    assertEquals(brita.moveUnits(unitSet, lu, content), false);

    //stay still
    assertEquals(brita.moveUnits(unitSet, brita, content), false);

    brita.removeEnemyProvince("Lugdunensis");
    assertEquals(brita.moveUnits(unitSet, ge, content), true); //2 points
    assertEquals(ballista.getRemainMovePoint(), 0);
    assertEquals(brita.moveUnit(ballista, lu, content), false);
    assertEquals(brita.moveUnit(ballista, brita, content), false);

  }
  @Test
  public void testTaxRate() {
    lu.setOwner(Rome);
    brita.setOwner(Rome);

    lu.setTaxRate(0.1);
    brita.setTaxRate(0.15);
    Rome.updateByTurn();
    assertEquals(lu.getWealthGrowth(), 110);
    assertEquals(brita.getWealthGrowth(), 100);
    assertEquals(Rome.getOwnerTreasury(), 126);

    ge.setOwner(Gaul);
    ge.setTaxRate(0.2);
    Gaul.updateByTurn();
    assertEquals(ge.getWealthGrowth(), 90);
    assertEquals(Gaul.getOwnerTreasury(), 100 + 18);

    Faction german = new Faction("German");
    bel.setOwner(german);
    bel.addMyUnit(archer);

    bel.setTaxRate(0.25);
    german.updateByTurn();
    assertEquals(bel.getWealthGrowth(), 70);
    assertEquals(german.getOwnerTreasury(), 100 + 18);
    assertEquals(archer.getMorale(), 4);
  }

